				&:hover, &.sXaDqb-W, &:focus, &.sXaDqb-Pb {
					background-color: dark3;
				}
			}
		}

		.RTMQvb::before {
			box-shadow: inset 0 0 0 1px accent;
		}
	}

	if (activationMode == 'System') {
		@media (prefers-color-scheme: dark) {
			activate();
		}
	} else if (activationMode == 'Always') {
		activate();
	}
}

